mapper.select:
SELECT 

FROM pool_batch_line a

WHERE a.id = #{id}

SELECT
FROM pool_batch_line a
AND a.pool_batch_id = #{poolBatch.id}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}

SELECT 

FROM pool_batch_line a






		ORDER BY ${page.orderBy}

mapper.update:
DELETE FROM pool_batch_line


		WHERE id = #{id}


		WHERE pool_batch_id = #{poolBatch.id}
